        public void pencarian(){
       Object[]baris = {"No.Daftar", "Nama", "Tempat Lahir", "Tgl Lahir", "Jenis Kelamin", "Agama",
                            "Jurusan", "Sekolah Asal", "Email", "Alamat Sekolah", "Nama Ayah",
                                "Pekerjaan", "tlp", "Alamat"};
        tabmode = new DefaultTableModel(null, baris);
        jTable1.setModel(tabmode);
        String sql = "select * from tb_pendaftaran where id like '%"+jTextField11.getText()+"%'";
        try {
            Connection konek = new koneksi().getConnection();
            Statement stat = konek.createStatement();
            ResultSet hasil = stat.executeQuery(sql);
            while (hasil.next()){
                String id = hasil.getString("id");
                String nama = hasil.getString("nama");
                String tempat_lahir = hasil.getString("tempat_lahir");
                String tgl_lahir = hasil.getString("tgl_lahir");
                String jenis_kelamin = hasil.getString("jenis_kelamin");
                String agama = hasil.getString("agama");
                String jurusan = hasil.getString("jurusan");
                String sekolah = hasil.getString("sekolah");
                String email = hasil.getString("email");
                String alamat_sekolah = hasil.getString("alamat_sekolah");
                String ayah = hasil.getString("ayah");
                String pekerjaan = hasil.getString("pekerjaan");
                String tlp = hasil.getString("tlp");
                String alamat = hasil.getString("alamat");
                String[]data ={id, nama, tempat_lahir, tgl_lahir, jenis_kelamin, agama,
                    jurusan, sekolah, email, alamat_sekolah, ayah, pekerjaan, tlp, alamat};
                tabmode.addRow(data);
            }
            konek.close();
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Menampilkan data gagal", "informasi", JOptionPane.INFORMATION_MESSAGE);
            
        }
    }//GEN-LAST:event_jButton5ActionPerformed

